mapper.select:
SELECT tl.*, u.user_name AS userName, s.NAME AS supplierName
FROM t_return_list tl
LEFT JOIN t_user u ON tl.user_id = u.id
LEFT JOIN t_supplier s ON tl.supplier_id = s.id


        and tl.return_number like concat('%', #{rlQuery.returnNumber}, '%')


        and tl.supplier_id = #{rlQuery.supplierId}


        and tl.state = #{rlQuery.state}


        and return_date >= #{rlQuery.startDate}


        and return_date <= #{rlQuery.endDate}
